import React, { useEffect, useState } from "react";
import NavBar from "./NavBar";
import Login from "../pages/Login";

function App() {
  const [user, setUser] = useState(null);
  const [tasks, setTasks] = useState([]);

  // State for title and description
  const [newTitle, setNewTitle] = useState("");
  const [newDescription, setNewDescription] = useState("");

  useEffect(() => {
    // Auto-login check
    const token = localStorage.getItem("token");
    if (token) {
      fetch("/me", {
        headers: {
          Authorization: `Bearer ${token}`
        }
      }).then((r) => {
        if (r.ok) {
          r.json().then((user) => {
            setUser(user);
            fetchTasks();
          });
        } else {
          // If token is invalid, clear it
          localStorage.removeItem("token");
        }
      });
    }
  }, []);

  function fetchTasks() {
    const token = localStorage.getItem("token");
    if (!token) return;

    fetch("/tasks", {
      headers: {
        Authorization: `Bearer ${token}`
      }
    }).then((r) => {
      if (r.ok) {
        r.json().then((data) => {
          // Access the tasks array from the paginated response
          if (data && Array.isArray(data.tasks)) {
            setTasks(data.tasks);
          } else {
            console.error("API did not return an array of tasks:", data);
            setTasks([]);
          }
        });
      } else {
        console.error("Failed to fetch tasks, status:", r.status);
      }
    });
  }

  const onLogin = (token, user) => {
    localStorage.setItem("token", token);
    setUser(user);
    fetchTasks();
  }

  function handleSubmitTask(e) {
    e.preventDefault();
    const token = localStorage.getItem("token");

    if (!token) {
      console.error("No token found!");
      return;
    }

    fetch("/tasks", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "Authorization": `Bearer ${token}`
      },
      // Send title and description
      body: JSON.stringify({ 
        title: newTitle,
        description: newDescription
      }),
    }).then((r) => {
      if (r.ok) {
        r.json().then((task) => {
          setTasks(Array.isArray(tasks) ? [...tasks, task] : [task]);
          // Clear inputs
          setNewTitle("");
          setNewDescription("");
        });
      } else {
        r.json().then(err => console.error("Server Error:", err));
      }
    });
  }

  function handleDeleteTask(id) {
    const token = localStorage.getItem("token");

    fetch(`/tasks/${id}`, {
      method: "DELETE",
      headers: {
        "Authorization": `Bearer ${token}`
      }
    }).then((r) => {
      if (r.ok) {
        // Remove task from state
        setTasks(tasks.filter((task) => task.id !== id));
      } else {
        console.error("Failed to delete task");
      }
    });
  }

  if (!user) return <Login onLogin={onLogin} />;

  return (
    <>
      <NavBar setUser={setUser} />
      <main style={{ padding: "20px", display: "flex", flexDirection: "column", alignItems: "center" }}>
        <h2>Welcome, {user.username}!</h2>

        {/* --- FORM STYLING UPDATED --- */}
        <form onSubmit={handleSubmitTask} style={{ 
          marginBottom: "20px",
          display: "flex",
          justifyContent: "center",
          gap: "10px",
          width: "100%",
          maxWidth: "600px"
        }}>
          <input
            type="text"
            value={newTitle}
            onChange={(e) => setNewTitle(e.target.value)}
            placeholder="Task title (required)..."
            style={{ padding: "8px", flexGrow: 1 }}
            required
          />
          <input
            type="text"
            value={newDescription}
            onChange={(e) => setNewDescription(e.target.value)}
            placeholder="Description..."
            style={{ padding: "8px", flexGrow: 1 }}
          />
          <button type="submit" style={{ padding: "8px" }}>Add Task</button>
        </form>

        <ul style={{ width: "100%", maxWidth: "600px", listStyle: "none", padding: 0 }}>
          {Array.isArray(tasks) && tasks.map((task) => (
            <li key={task.id} style={{ marginBottom: "10px", border: "1px solid #ccc", padding: "10px", borderRadius: "4px", display: "flex", justifyContent: "space-between", alignItems: "center" }}>
              <div>
                <strong>{task.title}</strong>: {task.description}
              </div>

              <button 
                onClick={() => handleDeleteTask(task.id)}
                style={{ marginLeft: "10px", padding: "2px 5px", color: "red" }}
              >
                Delete
              </button>
            </li>
          ))}
        </ul>
      </main>
    </>
  );
}

export default App;
