import React, { useEffect, useState } from "react";
import NavBar from "./NavBar";
import Login from "../pages/Login";

function App() {
  const [user, setUser] = useState(null);
  const [tasks, setTasks] = useState([]);

  // State for title and description
  const [newTitle, setNewTitle] = useState("");
  const [newDescription, setNewDescription] = useState("");

  useEffect(() => {
    // Auto-login check
    const token = localStorage.getItem("token");
    if (token) {
      fetch("/me", {
        headers: {
          Authorization: `Bearer ${token}`
        }
      }).then((r) => {
        if (r.ok) {
          r.json().then((user) => {
            setUser(user);
            fetchTasks();
          });
        } else {
          // If token is invalid, clear it
          localStorage.removeItem("token");
        }
      });
    }
  }, []);

  function fetchTasks() {
    const token = localStorage.getItem("token");
    if (!token) return;

    fetch("/tasks", {
      headers: {
        Authorization: `Bearer ${token}`
      }
    }).then((r) => {
      if (r.ok) {
        r.json().then((data) => {
          // Access the tasks array from the paginated response
          if (data && Array.isArray(data.tasks)) {
            setTasks(data.tasks);
          } else {
            console.error("API did not return an array of tasks:", data);
            setTasks([]);
          }
        });
      } else {
        console.error("Failed to fetch tasks, status:", r.status);
      }
    });
  }

  const onLogin = (token, user) => {
    localStorage.setItem("token", token);
    setUser(user);
    fetchTasks();
  }

  function handleSubmitTask(e) {
    e.preventDefault();
    const token = localStorage.getItem("token");

    if (!token) {
      console.error("No token found!");
      return;
    }

    fetch("/tasks", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "Authorization": `Bearer ${token}`
      },
      // Send title and description
      body: JSON.stringify({ 
        title: newTitle,
        description: newDescription
      }),
    }).then((r) => {
      if (r.ok) {
        r.json().then((task) => {
          setTasks(Array.isArray(tasks) ? [...tasks, task] : [task]);
          // Clear inputs
          setNewTitle("");
          setNewDescription("");
        });
      } else {
        r.json().then(err => console.error("Server Error:", err));
      }
    });
  }

  if (!user) return <Login onLogin={onLogin} />;

  return (
    <>
      <NavBar setUser={setUser} />
      <main style={{ padding: "20px" }}>
        <h2>Welcome, {user.username}!</h2>

        <form onSubmit={handleSubmitTask} style={{ marginBottom: "20px" }}>
          <input
            type="text"
            value={newTitle}
            onChange={(e) => setNewTitle(e.target.value)}
            placeholder="Task title (required)..."
            style={{ padding: "8px", marginRight: "5px" }}
            required
          />
          <input
            type="text"
            value={newDescription}
            onChange={(e) => setNewDescription(e.target.value)}
            placeholder="Description..."
            style={{ padding: "8px", marginRight: "5px" }}
          />
          <button type="submit" style={{ padding: "8px" }}>Add Task</button>
        </form>

        <ul>
          {Array.isArray(tasks) && tasks.map((task) => (
            <li key={task.id}>
              <strong>{task.title}</strong>: {task.description}
            </li>
          ))}
        </ul>
      </main>
    </>
  );
}

export default App;
